const initialState =  {
    books: [],
    loading: true,
    error: null,
    cartItems: [
        // {
        //     id: 1,
        //     name: "Book 1",
        //     count: 3,
        //     total: 150
        // },

        // {
        //     id: 2,
        //     name: "Book 2",
        //     count: 2,
        //     total: 70
        // }
    ],
    orderTotal: 220,
    increaseCount: false
}

const reducer = (state = initialState, action) => {

    console.log(action.type)

    switch(action.type) {
        case 'FETCH_BOOKS_REQUREST' :
            return {
                ...state,
                books: [],
                loading: true,
                error: null
            }
        case 'FETCH_BOOKS_SUCCESS' : 
            return {
                ...state,
                books: action.payload,
                loading: false,
                error: null
            };
        case 'FETCH_BOOKS_FAILURE' : 
            return {
                ...state,
                books: [],
                loading: false,
                error: action.payload
            };
        case 'BOOK_ADDED_TO_CART' :
            const bookId = action.payload
            const book = state.books.find((book) => book.id === bookId)
            const newItem = {
                id: book.id,
                name: book.title,
                count: 1,
                total: book.price
            }; 
 
            if( !state.increaseCount ) {
                return {
                    ...state,
                    increaseCount: true,
                    cartItems: [
                        ...state.cartItems,
                        newItem
                    ]
                }
            }else {
                // const onIncreaseCount = (newItem, bookId) => {
                //     if(newItem.id === bookId) {
                //         return true;
                //     }else {
                //         return false;
                //     }
                // } 

                const arr = state.cartItems;

                const idx = arr.findIndex((el) => el.id === bookId);
    
                const editItem = arr[idx];
        
                const newArr = {
                    ... editItem,
                    count: editItem.count + 1,
                    total: editItem.total + book.price
                };
        
                return {
                    ... state,
                    increaseCount: !state.increaseCount,
                    cartItems: [
                        ... arr.slice(0, idx),
                        newArr,
                        ... arr.slice(idx + 1)
                    ]
                } 
            } 
        default:
            return state;
    }

};

export default reducer;
