const initialState =  {
    books: [],
    important: false
}

const reducer = (state = initialState, action) => {

    switch(action.type) {
        case 'IMP':
            return action.payload(state.important);
        case 'INC':
            return state + 1;
        case 'DEC':
            return state - 1;
        default:
         return state;
    }

};

export default reducer;
