const reducer = (state = false, action) => {

    switch(action.type) {
        case 'IMP':
            return ( state + action.payload(state));
        case 'INC':
            return state + 1;
        case 'DEC':
            return state - 1;
        default:
         return state;
    }

};

export default reducer;
