const initialState =  {
    books: [],
    important: false
}

const reducer = (state = initialState, action) => {

    switch(action.type) {
        case 'BOOKS_LOADED' : 
            return state;
        case 'IMP':
            return action.payload(state);
        default:
         return state;
    }

};

export default reducer;
