export const err = (component, childer) => {
    return { 
        type: 'ERR', 
        payload: (state) => {
            if(state) {
                return component;
            } else if (!state) {
                return childer;
            }
        }
    };
}

export const inc = () => ({type: 'INC'});

export const dec = () => ({type: 'DEC'});
