const initialState =  {
    books: [],
    // important: false
}

const reducer = (state = initialState, action) => {

    switch(action.type) {
        case 'BOOKS_LOADED' : 
            return state.books;
        case 'IMP':
            return action.payload(state.important);
        default:
         return state;
    }

};

export default reducer;
