const initialState =  {
    books: [],
    loading: true,
    error: null,
    cartItems: [],
    orderTotal: 220
}

const updateCartItems = (cartItems, item, { itemIndex }) => {
    
    if (idx == -1) {
        return [
            ...cartItems,
            item
        ];
    };

    return [
        ...cartItems.slice(0, itemIndex),
        item,
        ...cartItems.slice(itemIndex + 1 )
    ];
};

const updateCartItem = ({ book, item = {} }) => {

    // const { book, item = {} } = data;
    
    const {
        id = book.id,
        title = book.title,
        count = 0,
        total = 0 } = item;

    return {
        id,
        title,
        count: count + 1,
        total: total + book.price
    }
};

const fun = (bookId, state) => {
    const book = state.books.find((book) => book.id === bookId);
    const itemIndex = state.cartItems.findIndex(({id}) => id === bookId);
    const item = state.cartItems[itemIndex];

    return {
        book,
        itemIndex,
        item
    }
}

const reducer = (state = initialState, action) => {

    console.log(action.type);

    switch(action.type) {
        case 'FETCH_BOOKS_REQUREST' :
            return {
                ...state,
                books: [],
                loading: true,
                error: null
            }
        case 'FETCH_BOOKS_SUCCESS' : 
            return {
                ...state,
                books: action.payload,
                loading: false,
                error: null
            };
        case 'FETCH_BOOKS_FAILURE' : 
            return {
                ...state,
                books: [],
                loading: false,
                error: action.payload
            };
        case 'BOOK_ADDED_TO_CART' :

            // let { book, itemIndex, item} = fun(action.payload, state);

            // console.log(book, itemIndex, item)

            let newItem = updateCartItem( fun(action.payload, state) );

            return {
                ...state,
                cartItems: updateCartItems(state.cartItems, newItem, fun(action.payload, state))
            }
        case 'INCREASE_BOOK_COUNT' :

            // let { book, itemIndex, item} = fun(action.payload, state);

            newItem = updateCartItem( fun(action.payload, state) );

        
            return {
                ...state,
                cartItems: updateCartItems(state.cartItems, newItem, fun(action.payload, state))
            }

        case 'DECREASE_BOOK_COUNT' :


            // return {
            //     ...state,
            //     cartItems: updateCartItems(state.cartItems, newItem, itemIndex)
            // }

        case 'DELETE_BOOK' :

            console.log('DELETE_BOOK');

        default:
            return state;
    }

};

export default reducer;
