const initialState =  {
    books: [],
}

const reducer = (state = initialState, action) => {

    switch(action.type) {
        case 'BOOKS_LOADED' : 
            return {
                books: action.payload
            };
        case 'IMP':
            return action.payload(false);
        default:
         return state;
    }

};

export default reducer;
