const initialState =  {
    books: [],
    loading: true,
    error: null,
    cartItems: [],
    orderTotal: 220,
    idState: null
}

const reducer = (state = initialState, action) => {

    console.log(action.type)

    switch(action.type) {
        case 'FETCH_BOOKS_REQUREST' :
            return {
                ...state,
                books: [],
                loading: true,
                error: null
            }
        case 'FETCH_BOOKS_SUCCESS' : 
            return {
                ...state,
                books: action.payload,
                loading: false,
                error: null
            };
        case 'FETCH_BOOKS_FAILURE' : 
            return {
                ...state,
                books: [],
                loading: false,
                error: action.payload
            };
        case 'BOOK_ADDED_TO_CART' :
            const bookId = action.payload
            const book = state.books.find((book) => book.id === bookId);
            const isItem = state.cartItems.findIndex((book) => book.id === bookId);

            console.log(isItem);

            if(isItem == -1 ) {
                const newItem = {
                    id: book.id,
                    name: book.title,
                    count: 1,
                    total: book.price
                }; 

                return {
                    ...state,
                    cartItems: [
                        ...state.cartItems,
                        newItem
                    ]
                }
            }else {

                const items = state.cartItems;

                const idx = items.findIndex((item) => item.id === bookId );

                const editItem = items[idx];

                const newItem = {
                    ...editItem,
                    count: editItem.count + 1,
                    total: editItem.total + book.price
                }

                return {
                    ... state,
                    cartItems: [
                        ...items.slice(0, idx),
                        newItem,
                        ...items.slice(idx, 0)
                    ]
                } 
            }
        default:
            return state;
    }

};

export default reducer;
