const initialState =  {
    books: [],
    loading: true,
    error: null,
    cartItems: [],
    orderTotal: 220
}

const updateCartItems = (cartItems, { newItem, itemIndex }) => {

    if(newItem.count == 0) {
        return [
            ...cartItems.slice(0, itemIndex),
            ...cartItems.slice(itemIndex + 1 )
        ];
    }
    
    if (itemIndex == -1) {
        return [
            ...cartItems,
            newItem
        ];
    };

    return [
        ...cartItems.slice(0, itemIndex),
        newItem,
        ...cartItems.slice(itemIndex + 1 )
    ];
};

const updateCartItem = (bookId, state, action) => {

    const book = state.books.find((book) => book.id === bookId);
    const itemIndex = state.cartItems.findIndex(({id}) => id === bookId);
    const item = state.cartItems == [] ? null : state.cartItems[itemIndex];

    const itemDefault = (book, item = {}) => {
        const {
            id = book.id,
            title = book.title,
            count = 0,
            total = 0 } = item;

        return {
            id,
            title,
            count,
            total
        };
    }

    const {
        id,
        title,
        count,
        total} = itemDefault(book, item);

    if(action == 'DECREASE_BOOK_COUNT') {
        return {
            newItem: {
                id: book.id,
                title: book.title,
                count: count - 1,
                total: total - book.price
            },
            itemIndex
        }
        
    }else if(action == 'BOOK_ADDED_TO_CART' || 'INCREASE_BOOK_COUNT') {
        return {
            newItem: {
                id,
                title,
                count: count + 1,
                total: total + book.price    
            },
            itemIndex
        }
    };
};

const reducer = (state = initialState, action) => {

    console.log(action.type);

    switch(action.type) {
        case 'FETCH_BOOKS_REQUREST' :
            return {
                ...state,
                books: [],
                loading: true,
                error: null
            }
        case 'FETCH_BOOKS_SUCCESS' : 
            return {
                ...state,
                books: action.payload,
                loading: false,
                error: null
            };
        case 'FETCH_BOOKS_FAILURE' : 
            return {
                ...state,
                books: [],
                loading: false,
                error: action.payload
            };
        case 'BOOK_ADDED_TO_CART' :

            return {
                ...state,
                cartItems: updateCartItems( state.cartItems, updateCartItem(action.payload, state, action.type) )
            }
        case 'INCREASE_BOOK_COUNT' :

            return {
                ...state,
                cartItems: updateCartItems( state.cartItems, updateCartItem(action.payload, state, action.type) )
            }
        case 'DECREASE_BOOK_COUNT' :

            return {
                ...state,
                cartItems: updateCartItems( state.cartItems, updateCartItem(action.payload, state, action.type) )
            }
        case 'DELETE_BOOK' :

            const id  = state.cartItems.findIndex(({ id }) => id === action.payload );

            return {
                ...state,
                cartItems: [
                    ...state.cartItems.slice(0, id),
                    ...state.cartItems.slice(id + 1)
                ]
            };
        default:
            return state;
    }

};

export default reducer;
