            //     };
        
            //     return [
            //         ... arr.slice(0, idx),
            //         newArr,
            //         ... arr.slice(idx + 1)
            //     ];
        
            // };

            return () => {
                if( !state.increaseCount ) {
                    return {
                        ...state,
                        increaseCount: true,
                        cartItems: [
                            ...state.cartItems,
                            mewItem
                        ]
                    }
                }else {

                    const arr = state.cartItems;

                    const idx = arr.findIndex((el) => el.id === bookId);
        
                    const editItem = arr[idx];
            
                    const newArr = {
                        ... editItem,
                        count: editItem.count + 1,
                        price: editItem.price + book.price
                    };
            
                    return{
                        ... state,
                        cartItems: [
                            ... arr.slice(0, idx),
                            newArr,
                            ... arr.slice(idx + 1)
                        ]
                    } 
                } 
            };    
        default:
            return state;
    }

};

export default reducer;
