export const booksLoaded = ( newBooks ) => {
    return {
        type: 'BOOKS_LOADED',
        payload: newBooks
    }
}

export const booksRequresd = () => {
    return {
        type: 'BOOKS_REQURESD'
    }
}

export const booksError = ( error ) => {
    return {
        type: 'BOOKS_Error',
        payload: error
    }
}

export const imp = () => {
    return  {
        type: 'IMP',
        payload: (background) => {
            let state = !background;
            if(state) {
                return 'important'; 
            } else {
                return;
            }
        }
    }
}

export const inc = () => ({type: 'INC'});

export const dec = () => ({type: 'DEC'});
