const initialState =  {
    books: [],
    loading: true,
    error: null,
    cartItems: [],
    orderTotal: 220,
    idState: null
}

const updateCartItems = (cartItems, item, idx) => {
    
    if (idx == -1) {
        return [
            ...cartItems,
            item
        ];
    };

    return [
        ...cartItems.slice(0, idx),
        item,
        ...cartItems.slice(idx + 1 )
    ];
};

const updateCartItem = (book, item = {}) => {
    
    const {
        id = book.id,
        title = book.title,
        count = 0,
        total = 0 } = item;

    return {
        id,
        title,
        count: count + 1,
        total: total + book.price
    }
};

const fun = (bookId, state) => {
    const book = state.books.find((book) => book.id === bookId);
    const itemIndex = state.cartItems.findIndex(({id}) => id === bookId);
    const item = state.cartItems[itemIndex];

    return {
        book,
        itemIndex,
        item
    }
}

const reducer = (state = initialState, action) => {

    console.log(action.type);
    const { book, itemIndex, item} = fun(action.payload, state);

    switch(action.type) {
        case 'FETCH_BOOKS_REQUREST' :
            return {
                ...state,
                books: [],
                loading: true,
                error: null
            }
        case 'FETCH_BOOKS_SUCCESS' : 
            return {
                ...state,
                books: action.payload,
                loading: false,
                error: null
            };
        case 'FETCH_BOOKS_FAILURE' : 
            return {
                ...state,
                books: [],
                loading: false,
                error: action.payload
            };
        case 'BOOK_ADDED_TO_CART' :
            
            const newItem = updateCartItem(book, item);

            return {
                ...state,
                cartItems: updateCartItems(state.cartItems, newItem, itemIndex)
            }
        case 'INCREASE_BOOK_COUNT' : 

            const newItem_1 = {
                id: book.id,
                title: book.title,
                count: book.count + 1,
                total: book.total + book.price
            };
        
            return {
                ...state,
                cartItems: updateCartItems(state.cartItems, newItem_1, itemIndex)
            }

        case 'DECREASE_BOOK_COUNT' :

            const newItem_2 = {
                id: book.id,
                title: book.title,
                count: book.count - 1,
                total: book.total - book.price
            };

            return {
                ...state,
                cartItems: updateCartItems(state.cartItems, newItem_2, itemIndex)
            }

        case 'DELETE_BOOK' :

            console.log('DELETE_BOOK');

        default:
            return state;
    }

};

export default reducer;
