const reducer = (state = "btn btn-primary btn-lg ", action) => {

    switch(action.type) {
        case 'IMP':
            return state + action.payload();
        case 'INC':
            return state + 1;
        case 'DEC':
            return state - 1;
        default:
         return state;
    }

};

export default reducer;
