const initialState =  {
    books: [
        {   
            id: 1, 
            title: 'Production-Ready Micorservices',
            author: 'Susan J. Fowler' },
        {   
            id: 2,
            title: 'Release It!',
            author: 'Michael T. Nygard' }
    ],
    important: false
}

const reducer = (state = initialState, action) => {

    switch(action.type) {
        case 'BOOKS_LOADED' : 
            return state.books;
        case 'IMP':
            return action.payload(state.important);
        default:
         return state;
    }

};

export default reducer;
